import React from "react";
import "./About.css";
import Button2 from "../button/Button2";
import image from "../../images/instagram1/pastry.jpg";
const About = () => {
  return (
    <section className="about">
      <article className="about__container">
        <div className="col1">
          <div className="row2">
            <aside className="about__text">
              <h3>The Ceccaldi cutlery</h3>
              <h2>A family business</h2>
              <p>
                Borscht N Tears prides itself as being the oldest Russian
                restaurant in the United Kingdom, and one of the oldest in the
                world, originally opening our doors in 1965.
              </p>
              <div className="about-btns">
                <Button2 className="btn2" text={"Discover more"}></Button2>
              </div>
            </aside>
          </div>
        </div>
        <div className="col2">
          <div className="row2">
            <div className="about__image-container">
              <img src={image} alt="" />
            </div>
          </div>
        </div>
      </article>
    </section>
  );
};

export default About;
