import React, { useState } from "react";
import axios from "axios";
import "./ContactForm.css";

import Button2 from '../button/Button2'
const ContactForm = () => {
  const [serverState, setServerState] = useState({
    submitting: false,
    status: null,
  });
  const handleServerResponse = (ok, msg, form) => {
    setServerState({
      submitting: false,
      status: { ok, msg },
    });
    if (ok) {
      form.reset();
    }
  };
  const handleOnSubmit = (e) => {
    e.preventDefault();
    const form = e.target;
    setServerState({ submitting: true });
    axios({
      method: "post",
      url: "https://formspree.io/YOUR_FORM_ID",
      data: new FormData(form),
    })
      .then((r) => {
        handleServerResponse(true, "Thanks!", form);
      })
      .catch((r) => {
        handleServerResponse(false, r.response.data.error, form);
      });
  };
  return (
    <section className="contact-form">
      <div className="contact-form__container">
        <form className="contact-form__form" onSubmit={handleOnSubmit}>
        <h1 className="contact-form__title">Contact us</h1>
          <label htmlFor="Subject">Subject</label>
          <select name="id_contact" >
            <option value="Restaurant Bookings">Restaurant Bookings</option>
            <option value="Karaoke Bookings">Karaoke Bookings</option>
            <option value="Sales">Sales</option>
            <option value="Other">Other</option>
          </select>
          <label htmlFor="email">Email Address</label>
          <input placeholder="Your@email.com" id="email" type="email" name="email" required />
          <label htmlFor="message">Message:</label>
          <textarea placeholder="How can we help?" id="message" name="message"></textarea>
          <Button2 text={"Submit"} type="submit" disabled={serverState.submitting}>

          </Button2>
          {serverState.status && (
            <p className={!serverState.status.ok ? "errorMsg" : ""}>
              {serverState.status.msg}
            </p>
          )}
        </form>
      </div>
    </section>
  );
};

export default ContactForm;
