import React from "react";
import Environment from "./environment/Environment";
import History from "./history/History";
import russia from "../../images/russia.jpg";
import face from "../../images/female-faces/Face/face-05.svg";
import crembrule from "../../images/instagram1/crembrule.jpg";

const About = () => {
  return (
    <main className="about">
      <History />
      <Environment
        title1={"Bringing Russia to you"}
        subtitle1={"Authenticity at it's finest"}
        desc1={
          " From the design to the final crafting of a knife, many work techniques are used — the processing of steel, of course, but also that of wood, horn, or precious metals. It is the combination of these particular techniques that gives Ceccaldi knife-makers this unique know-how."
        }
        lineArt={face}
        title2={"A unique experience every time"}
        image3={crembrule}
        squareImage={russia}
        desc2={
          " Borscht N Tears prides itself as being the oldest Russian restaurant in the United Kingdom, and one of the oldest in the  world, originally opening our doors in 1965. We are a small  local business, that strives to supply high quality pan Russian cuisine using only the freshest seasonal ingredients. We have only the best live musicians performing in the restaurant, to  create an unforgettable experience."
        }
      />
    </main>
  );
};

export default About;
