import React from 'react';
import './Button.css';
import { Link } from 'react-router-dom';

const STYLES = ['btn--primary', 'btn--outline', 'btn--test'];

const SIZES = ['btn--medium', 'btn--large'];

export const Button = ({
  children,
  type,
  onClick,
}) => {


  return (
    <Link to='/sign-up' className='btn-mobile'>
      <button
        className="btn"
        onClick={onClick}
        type={type}
      >
        {children}
      </button>
    </Link>
  );
};
