import React from "react";
import { useForm } from '@formspree/react';
import "./ContactForm.css";

import Button2 from "../button/Button2";
const ContactForm = () => {

  const [state, handleSubmit] = useForm('https://formspree.io/f/xknpljlk');
  if (state.succeeded) {
    return <div>Thank you for signing up!</div>;
  }
  return (
    <section className="contact-form">
      <div className="contact-form__container">
        <form className="contact-form__form" onSubmit={handleSubmit}>
          <h1 className="contact-form__title">Contact us</h1>
          <label htmlFor="Subject">Subject</label>
          <select name="id_contact">
            <option value="Restaurant Bookings">Restaurant Bookings</option>
            <option value="Karaoke Bookings">Karaoke Bookings</option>
            <option value="Sales">Sales</option>
            <option value="Other">Other</option>
          </select>
          <label htmlFor="email Address">Email Address</label>
          <input
            placeholder="Your@email.com"
            id="email"
            type="email"
            name="email"
            required
          />
          <label htmlFor="message">Message:</label>
          <textarea
            placeholder="How can we help?"
            id="message"
            name="message"
          ></textarea>
          <button
            text={"Submit"}
            type="submit"
            disabled={state.submitting}
          ></button>

        </form>
      </div>
    </section>
  );
};

export default ContactForm;
